mapper.select:
SELECT permission.* FROM permission
INNER JOIN role_permission ON role_permission.permission_id = permission.id AND role_permission.role_id = #{filter.roleId}
WHERE permission.removed_at IS NULL
AND permission.name LIKE "%"#{filter.name}"%"
AND permission.code LIKE "%"#{filter.code}"%"
AND permission.resource LIKE "%"#{filter.resource}"%"
AND permission.action = #{filter.action}
ORDER BY
user.#{item.fieldName} #{item.sort}
LIMIT #{offset},#{size}20
